<span class="line"><span style="color:#BABED8;">        self</span><span style="color:#89DDFF;">.</span><span style="color:#F07178;">val</span><span style="color:#89DDFF;">:</span><span style="color:#FFCB6B;"> int</span><span style="color:#89DDFF;"> =</span><span style="color:#BABED8;"> val                </span><span style="color:#676E95;font-style:italic;"># 节点值</span></span>
